package practice4;
import java.util.Scanner;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		int x = 2;
		
		switch(x) {
		case 0 : 
			System.out.println("0");
		
	    case 1: 
		    System.out.println("1");
	    
	    case 2 : 
			System.out.println("2");
		
		}
	


	}

}
